mapper.select:
SELECT *
FROM profinishinfo
WHERE projectId = #{projectId}
    and empId = #{empId}

mapper.delete:
delete
from profinishinfo
WHERE projectId = #{projectId}
  and empId = #{empId}
    and empPostion = 0

delete
from profinishinfo
WHERE projectId = #{projectId}
  and empId = #{empId}
  and empPostion = 1
