mapper.select:
SELECT *
FROM taskfinishinfo
WHERE subtaskId = #{subTaskID}
  and empId = #{EmpID}

SELECT *
FROM taskfinishinfo
WHERE subtaskId = #{subTaskID}
  and empId = #{EmpID} and doType = #{doType}

SELECT *
FROM taskfinishinfo
WHERE subtaskId = #{subTaskID} and doType = #{doType}

SELECT distinct * FROM taskfinishinfo, subtask WHERE taskfinishinfo.projectId = #{proID}
                                        AND taskfinishinfo.`subtaskId` = subtask.`subtaskId` AND subtask.`subtaskState` = 3
                                        AND  subtask.subtaskid NOT IN (
        SELECT taskfinishinfo.`subtaskId` FROM taskfinishinfo WHERE taskfinishinfo.`doType` = 2
    );

SELECT distinct * FROM taskfinishinfo, subtask WHERE taskfinishinfo.projectId = #{proID}
                                        AND taskfinishinfo.`subtaskId` = subtask.`subtaskId`
                                        AND subtask.`subtaskState` = 3
                                        and taskfinishinfo.empId = #{EmpID}

SELECT distinct * FROM taskfinishinfo, subtask WHERE taskfinishinfo.projectId = #{proID}
                                        AND taskfinishinfo.`subtaskId` = subtask.`subtaskId`
                                        and taskfinishinfo.empId = #{EmpID}

mapper.update:
update taskfinishinfo
set projectId               = #{projectId},
    doType                  = #{doType},
    subTaskOutSourceEndTime = #{subTaskOutSourceEndTime}
where taskfinishinfo.subtaskId = #{subTaskId}

mapper.delete:
delete
from taskfinishinfo
where subtaskId = #{subTaskID}
    and empId = #{EmpID}
    and doType = #{doType}
